	public void sendDirectMessage(long userId, String message)
			throws TwitterException {
		int count = 0;
		int start = 0;
		int end = 140;

		String[] SplitMessage = new String[30];

		System.out.println("Message Length: " + message.length());

		if (message.length() > 140) {
			while (end < message.length()) {
				SplitMessage[count] = message.substring(start, end);
				count++;
				start += 140;
				end += 140;
				System.out.println("Start: " + start);
				System.out.println("End: " + end);
			}

			end -= 140;
			SplitMessage[count] = message.substring(end, message.length());
		}

		count = 0;

		while (SplitMessage[count] != null) {
			twitter.sendDirectMessage(userId, SplitMessage[count]);
			count++;
			System.out.println("Test2");
		}
		if (SplitMessage[0] == null) {
			twitter.sendDirectMessage(userId, message);
			System.out.println("Test3");
		}
	}

	private final Location[] getTrendsLocations() {
		ResponseList<Location> locations = null;
		try {
			locations = twitter.getAvailableTrends();
		} catch (TwitterException e) {
			e.printStackTrace();
		}
		trendsLocations = new Location[locations.size()];
		for (int i = 0; i < locations.size(); i++) {
			trendsLocations[i] = locations.get(i);
		}

		return trendsLocations;
	}

	public final String[] getTrendsLocationNames() {
		this.getTrendsLocations();
		String[] locationnames = new String[trendsLocations.length];
		for (int i = 0; i < trendsLocations.length; i++) {
			locationnames[i] = trendsLocations[i].getName();
		}
		return locationnames;
	}

	public final Trends getLocationTrends(String locationName) {
		for (int i = 0; i < trendsLocations.length; i++) {
			if (locationName.equals(trendsLocations[i].getName())) {
				try {
					return twitter
							.getPlaceTrends(trendsLocations[i].getWoeid());
				} catch (TwitterException e) {
					e.printStackTrace(); // ////////////////////////TO-DO ERROR
											// HANDLING
				}
			}
		}
		return null;
	}

	public List<DirectMessage> getDirectMessages() throws TwitterException {
		Paging paging = new Paging(1, 50);

		return twitter.getDirectMessages(paging);
	}

	public void deleteMessage(long MessageId) throws TwitterException {
		twitter.destroyDirectMessage(MessageId);
	}
}

package model;

import java.math.BigInteger;
import java.util.Date;

/**
 * Creates the Arraylist Parmaters called Tweet and this
 * class impliments the MyTweet interface.
 * 
 * @author Ben
 */
public class Tweet implements MyTweet {
	/**  */
	private BigInteger id;
	/** Stores the tweet date.*/
	private Date date;
	/** Stores the username.*/
	private String loginName;
	/** Stores a user's actual name. */
	private String displayName;
	/** Stores the text of a Status. */
	private String text;
	/** Stores the number of friends. */
	private int friendsCount;
	/** Stores the number of followers. */
	private int followersCount;

//	private String fave;
//	private String favorite;
	private boolean favorite;
	/**
	 * The Tweet constructor.
	 * 
	 * @param date - stores the tweet date.
	 * @param loginName - stores the username
	 * @param displayName - stores the actual name
	 * @param friendsCount - stores amount of friends
	 * @param followersCount - stores amount of followers
	 * @param text - stores the text of the tweet
	 */
	public Tweet(final Date dte, final String lginName,
			final String dsplayName, final int frendsCount,
			final int fllowersCount, final String txt,
			final boolean fave) {
		this.date = dte;
		this.loginName = lginName;
		this.displayName = dsplayName;
		this.friendsCount = frendsCount;
		this.followersCount = fllowersCount;
		this.text = txt;
		this.favorite = fave;
	}

	
	/**
	 * Gets the user id.
	 * @return id - the user id
	 */
	public final  BigInteger getId() {
		return id;
	}
	
	/**
	 * Gets the date.
	 * @return date - date of the tweet
	 */
	public final Date getCreatedAt() {
		return date;
	}
	
	/**
	 * Gets the login name.
	 * @return loginName - username of tweeter
	 */
	public final String getLoginName() {
		return loginName;
	}
	
	/**
	 * Gets the display name.
	 * @return displayName - actual name of tweeter
	 */
	public final String getDisplayName() {
		return displayName;
	}
	
	/**
	 * Gets the status text.
	 * @return text - the text of a tweet
	 */
	public final String getText() {
		return text;
	}
	
	/**
	 * Gets the friends count.
	 * @return friendsCount - the number of friends
	 */
	public final int getFriendsCount() {
		return friendsCount;
	}
	
	/**
	 * Gets the followers count.
	 * @return followersCount - the number of followers
	 */
	public final int getFollowersCount() {
		return followersCount;
	}
	@Override
	public boolean isFavorite() {
		// TODO Auto-generated method stub
		return favorite;
	}
}

package model;
import java.math.BigInteger;
import java.util.Date;


/**
 * MyTweet creates the basic interface for a users status
 * 
 * @author Ben
 *
 */
public interface MyTweet {
	BigInteger getId();
	
	Date getCreatedAt();
	
	String getLoginName();
	
	String getDisplayName();
	
	String getText();
	
	int getFriendsCount();
	
	int getFollowersCount();
	
	boolean isFavorite();
}

package model;

import java.io.File;
import java.io.IOException;
import java.util.ArrayList;

import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.ParserConfigurationException;
import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerException;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.dom.DOMSource;
import javax.xml.transform.stream.StreamResult;

import org.w3c.dom.Attr;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;
import org.xml.sax.SAXException;

public class FavoritesUtility {

	private ArrayList<String> stringList;
	private String curUser;

	public FavoritesUtility() {
		setStringList(new ArrayList<String>());
	}

	private void writeToFile() throws ParserConfigurationException,
			TransformerException {
		DocumentBuilderFactory docFactory = DocumentBuilderFactory
				.newInstance();
		DocumentBuilder docBuilder = docFactory.newDocumentBuilder();

		// root elements
		Document doc = docBuilder.newDocument();
		Element rootElement = doc.createElement("Favorites");
		doc.appendChild(rootElement);

		// user elements
		Element user = doc.createElement("User");
		rootElement.appendChild(user);

		// set attribute to user element
		Attr attr = doc.createAttribute("id");
		attr.setValue(curUser);
		user.setAttributeNode(attr);

		// write the content into xml file
		TransformerFactory transformerFactory = TransformerFactory
				.newInstance();
		Transformer transformer = transformerFactory.newTransformer();
		DOMSource source = new DOMSource(doc);
		StreamResult result = new StreamResult(new File(""
				+ ".\\Twitter4j\\favorites\\" + curUser + ".xml"));

		transformer.transform(source, result);

		System.out.println("File saved!");

	}

	private void readFromFile(final String username)
			throws ParserConfigurationException, SAXException, IOException {
		File fXmlFile = new File("..\\Twitter4j\\favorites\\" + username
				+ ".xml");
		curUser = username;
		if (fXmlFile.exists()) {
			DocumentBuilderFactory dbFactory = DocumentBuilderFactory
					.newInstance();
			DocumentBuilder dBuilder = dbFactory.newDocumentBuilder();
			Document doc = dBuilder.parse(fXmlFile);

			doc.getDocumentElement().normalize();
			System.out.println("Root element: "
					+ doc.getDocumentElement().getNodeName());

			NodeList nList = doc.getElementsByTagName("staff");

			System.out.println("----------------------------");

			for (int temp = 0; temp < nList.getLength(); temp++) {

				Node nNode = nList.item(temp);

				if (nNode.getNodeType() == Node.ELEMENT_NODE) {

					Element eElement = (Element) nNode;

					addFavoriteUser(eElement.getAttribute("id"));

				}
			}
		}
	}

	public final void saveFavorites() {
		try {
			writeToFile();
		} catch (ParserConfigurationException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (TransformerException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	}

	public final void loadFavorites(final String username) {
		try {
			readFromFile(username);
		} catch (ParserConfigurationException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (SAXException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	}

	public final void addFavoriteUser(final String name) {
		stringList.add(name);
	}

	public final void removeFavorite(final String name) {
		stringList.remove(name);
	}

	public final ArrayList<String> getStringList() {
		return stringList;
	}

	public final void setStringList(final ArrayList<String> strList) {
		this.stringList = strList;
	}

	public final void clearList() {
		stringList.clear();
	}
}

