	/**
	 * Packs and sets the GUI
	 * 
	 * @throws Exception
	 */
	public TwitterGUI() throws Exception {
		guiFrame = new JFrame();
		engine = new TwitterEngine();
		guiFrame.setSize(800, 450);

		engine.login();

		menuInit();
		postTimeTabInit();
		followerTabInit();
		favoritesTabInit();
		MyTweetsTabInit();
		FriendsListTabInit();
		profileTabInit();
		trendsTabInit();

		tabs = new JTabbedPane();

		tabs.addTab("Profile", ProfilePanel);
		tabs.addTab("Post Tweet/Timeline", PostTimePanel);
		tabs.addTab("Followers", TwitResults);
		tabs.addTab("Friends List", FriendsListPanel);
		tabs.addTab("Favorites", FavoritesPanel);
		tabs.addTab("Your Tweets", MyTweetsPanel);
		tabs.addTab("Trends", trendsPanel);

		guiFrame.add(tabs);
		guiFrame.setTitle("Twitter for "
				+ engine.getRealName(engine.getuserid()));

		final URL url = new URL(
				"http://jonbennallick.co.uk/wp-content/uploads/2012/11/Twitter-Logo-Icon-by-Jon-Bennallick-02.png");
		guiFrame.setIconImage(ImageIO.read(url));

		guiFrame.setVisible(true);
	}

	private void FriendsListTabInit() throws TwitterException {
		FriendsListPanel = new JPanel();
		FriendsListPanel.setLayout(new BorderLayout());

		JPanel ButtonsPanel = new JPanel();

		VeiwProfile = new JButton("View Profile");
		RemoveFriend = new JButton("Remove Friend");
		UpdateFriendsList = new JButton("Update");
		ButtonListener listener = new ButtonListener();

		VeiwProfile.addActionListener(listener);
		RemoveFriend.addActionListener(listener);
		UpdateFriendsList.addActionListener(listener);

		users = engine.getFriendsList(engine.getuserid());
		FriendsNamesList = new String[users.size()];
		userIds = new long[users.size()];
		int count = 0;

		for (User user : users) {
			FriendsNamesList[count] = user.getName();
			userIds[count] = user.getId();
			count++;
		}

		FriendsList = new JList<String>(FriendsNamesList);

		ButtonsPanel.add(UpdateFriendsList);
		ButtonsPanel.add(VeiwProfile);
		ButtonsPanel.add(RemoveFriend);

		JScrollPane scrollpane = new JScrollPane(FriendsList);
		FriendsListPanel.add(scrollpane, BorderLayout.CENTER);
		FriendsListPanel.add(ButtonsPanel, BorderLayout.PAGE_END);
	}

	private final void trendsTabInit() {

		trendsPanel = new JPanel();
		trendsPanel.setLayout(new BorderLayout());
		trendsArea = new JTextArea(20, 40);
		trendsArea.setLineWrap(true);
		trendsArea.setEditable(false);

		trendsPanel.add(trendsArea);
	}

	private final void updateTrendsArea() {
		trendsArea.setText("");
		for (Trend trend : trendslist) {
			trendsArea.append(trend.getName());
			trendsArea.append("\n");
		}
	}

	private void MyTweetsTabInit() throws TwitterException {
		DefaultListModel<String> model = new DefaultListModel<String>();
		list = new JList<String>(model);
		list.setFixedCellHeight(50);

		MyTweetsPanel = new JPanel();
		MyTweetsPanel.setLayout(new BorderLayout());
		JPanel ButtonsPanel = new JPanel();

		String html1 = "<html><body style='width: ";
		String html2 = "px'>";

		MyTweetsFavoriteButton = new JButton("Favorite");
		DeleteMyTweetButton = new JButton("Delete Tweet");
		UpdateMyTweetsList = new JButton("Update");
		ButtonListener listener = new ButtonListener();

		MyTweetsFavoriteButton.addActionListener(listener);
		DeleteMyTweetButton.addActionListener(listener);
		UpdateMyTweetsList.addActionListener(listener);

		statuses = engine.getMyTweets();
		MyTweetsStatusIds = new long[statuses.size()];
		int count = 0;

		for (Status status : statuses) {
			model.addElement(html1 + "550" + html2 + status.getText());
			MyTweetsStatusIds[count] = status.getId();
			count++;
		}

		JScrollPane scrollpane = new JScrollPane(list);

		ButtonsPanel.add(UpdateMyTweetsList);
		ButtonsPanel.add(MyTweetsFavoriteButton);
		ButtonsPanel.add(DeleteMyTweetButton);

		MyTweetsPanel.add(scrollpane, BorderLayout.CENTER);
		MyTweetsPanel.add(ButtonsPanel, BorderLayout.PAGE_END);

	}

	private final void profileTabInit() throws IllegalStateException,
			TwitterException, MalformedURLException {

		Border blackline = BorderFactory.createLineBorder(Color.black);

		ProfilePanel = new JPanel();

		ProfilePanel.setLayout(new GridLayout(2, 1));

		JPanel top = new JPanel();
		top.setLayout(new GridLayout(1, 1));
		top.setBorder(blackline);
		top.setSize(200, 200);

		JPanel top2 = new JPanel();
		top2.setLayout(new GridLayout(2, 1));
		top.setBorder(blackline);

		JPanel bottom = new JPanel();
		bottom.setBorder(blackline);
		JPanel user = new JPanel();
		user.setLayout(new BorderLayout());
		user.setBorder(blackline);
		JPanel login = new JPanel();
		login.setBorder(blackline);

		ButtonListener listener = new ButtonListener();

		ImageIcon img = engine.getProfileImage();

		JLabel userIcon = new JLabel(img);
		userIcon.setBorder(blackline);
		JLabel screenname = new JLabel(engine.getScreenName());
		screenname.setHorizontalAlignment(SwingConstants.CENTER);
		screenname.setBorder(blackline);
		screenname.setSize(20, 12);
		JLabel realname = new JLabel(engine.getRealName(engine.getuserid()));
		realname.setBorder(blackline);
		realname.setHorizontalAlignment(SwingConstants.CENTER);
		realname.setSize(20, 12);
		JLabel tweets = new JLabel("Tweets: " + engine.getTweets());
		JLabel followers = new JLabel("Followers: "
				+ engine.getFollowersCount());
		JLabel following = new JLabel("Following: "
				+ engine.getFollowingCount());
		JLabel rateLimit = new JLabel("Rate Limit: " + engine.getRateLimit());
		JLabel rateLimitRemaining = new JLabel("Rate Limit Remaining: "
				+ engine.getRateLimitRemaining());

		top.add(userIcon);
		top2.add(realname);
		top2.add(screenname);

		bottom.add(tweets);
		bottom.add(followers);
		bottom.add(following);
		bottom.add(rateLimit);
		bottom.add(rateLimitRemaining);

		user.add(top, BorderLayout.PAGE_START);
		user.add(top2, BorderLayout.CENTER);
		user.add(bottom, BorderLayout.PAGE_END);

		LoginButton = new JButton("Log In");
		LoginButton.addActionListener(listener);
		SignoutButton = new JButton("Sign Out");
		SignoutButton.addActionListener(listener);
		ViewMessagesButton = new JButton("View Messages");
		ViewMessagesButton.addActionListener(listener);

		login.add(ViewMessagesButton);
		login.add(LoginButton);
		login.add(SignoutButton);

		ProfilePanel.add(user, BorderLayout.CENTER);
		ProfilePanel.add(login, BorderLayout.PAGE_END);
	}

	/**
	 * Contains the Panels in the Operation Section. The combobox allows
	 * selection bettween the different options.
	 * 
	 * @throws TwitterException
	 */
	private void postTimeTabInit() throws TwitterException {
		ButtonListener listener = new ButtonListener();
		UpdatePanel = new JPanel();
		PostTimePanel = new JPanel();

		PostTimePanel.setLayout(new BorderLayout());

		DefaultListModel<String> model = new DefaultListModel<String>();

		String html1 = "<html><body style='width: ";
		String html2 = "px'>";

		updateTextBox = new JTextField(50);

		TweetButton = new JButton("Post Tweet");
		TweetButton.addActionListener((listener));
		updateTimelineButton = new JButton("Update");
		updateTimelineButton.addActionListener(listener);
		TimelineFavoriteButton = new JButton("Favorite");
		TimelineFavoriteButton.addActionListener(listener);

		UpdatePanel.add(updateTextBox);
		UpdatePanel.add(TweetButton);
		UpdatePanel.add(updateTimelineButton);

		MyTimeLineStatusIds = new long[200];
		int count = 0;
		statuses = engine.getTimeline();

		TimelineList = new JList<String>(model);
		TimelineList.setFixedCellHeight(50);

		for (Status status : statuses) {
			model.addElement(html1 + "550" + html2
					+ engine.getRealName(status.getUser().getId()) + ": "
					+ status.getText() + "\n");
			MyTimeLineStatusIds[count] = status.getId();
			count++;
		}

		JScrollPane TimeLinePane = new JScrollPane(TimelineList);
		PostTimePanel.add(UpdatePanel, BorderLayout.PAGE_START);
		PostTimePanel.add(TimeLinePane, BorderLayout.CENTER);
		PostTimePanel.add(TimelineFavoriteButton, BorderLayout.PAGE_END);
	}

	/**
	 * Creates the Eastern Panel using the class TwitterResultsPanel();
	 */
	private void followerTabInit() {

		GridLayout resultLayout = new GridLayout(2, 2, 5, 10);
		TwitResults = new JPanel();
		TwitResults.setLayout(resultLayout);

		// Table Creation
		table = new JTable();
		table.setToolTipText("");
		table.setSelectionMode(javax.swing.ListSelectionModel.SINGLE_SELECTION);
		table.getTableHeader().setReorderingAllowed(false);

		table.addMouseListener(new java.awt.event.MouseAdapter() {
			public void mouseClicked(final java.awt.event.MouseEvent evt) {
				tableMouseClicked();
			}
		});
		table.setPreferredScrollableViewportSize(new Dimension(500, 140));
		JScrollPane scrollPane = new JScrollPane();
		tableTitle = BorderFactory.createTitledBorder("Statuses");

		table.setModel(engine.getModel());
		scrollPane
				.setVerticalScrollBarPolicy(JScrollPane.VERTICAL_SCROLLBAR_ALWAYS);
		// attaches table to the scroll pane
		scrollPane.setViewportView(table);
		scrollPane.setBorder(tableTitle);
		TwitResults.add(scrollPane);

		// Text Area
		textArea = new JTextArea(7, 40);
		textArea.setEditable(false);
		textTitle = BorderFactory.createTitledBorder("Status Text");
		textArea.setLineWrap(true);
		textArea.setBorder(textTitle);
		JScrollPane textPane = new JScrollPane();
		textPane.setVerticalScrollBarPolicy(JScrollPane.VERTICAL_SCROLLBAR_ALWAYS);
		textPane.setViewportView(textArea);
		TwitResults.add(textPane);
	}

	private void tableMouseClicked() {
		int index = table.getSelectedRow();
		if (index != -1) {
			String output = engine.getDisplayStatis(index);
			setStatus(output);
		}
	}

	/**
	 * Orders what needs to be displayed with in the Status text area.
	 * 
	 * @param object
	 * 
	 * @param output
	 *            - A string of the users current status.
	 */
	private void setStatus(final String output) {
		textArea.setText(output);
	}

	public void favoritesTabInit() throws TwitterException {
		FavoritesPanel = new JPanel();
		FavoritesPanel.setLayout(new BorderLayout());

		JPanel ButtonsPanel = new JPanel();

		ButtonListener listener = new ButtonListener();

		UpdateFavoritesButton = new JButton("Update");
		UpdateFavoritesButton.addActionListener(listener);
		DeleteFavoriteButton = new JButton("Delete");
		DeleteFavoriteButton.addActionListener(listener);

		statuses = engine.getFavoriteTweets();
		MyFavoriteStatusIds = new long[200];

		favModel = new DefaultListModel<String>();
		FavoritesList = new JList<String>(favModel);
		FavoritesList.setFixedCellHeight(50);

		updateFavoritesList();
		JScrollPane FavoritePane = new JScrollPane(FavoritesList);

		ButtonsPanel.add(UpdateFavoritesButton);
		ButtonsPanel.add(DeleteFavoriteButton);

		FavoritesPanel.add(FavoritePane, BorderLayout.CENTER);
		FavoritesPanel.add(ButtonsPanel, BorderLayout.PAGE_END);
	}

	private final void updateFavoritesList() throws TwitterException {
		String html1 = "<html><body style='width: ";
		String html2 = "px'>";
		statuses = engine.getFavoriteTweets();
		int count = 0;
		for (Status status : statuses) {
			favModel.addElement(html1 + "400" + html2
					+ status.getUser().getName() + ":" + status.getText());
			MyFavoriteStatusIds[count] = status.getId();
			count++;
		}
		FavoritesList.updateUI();
	}

	/**
	 * menuInit() geneartes the JMenuBar to be used.
	 */
	private void menuInit() {
		menu = new JMenuBar();
		// Creates the File Menu
		file = new JMenu("File");
		fileDeleteStatus = new JMenuItem("Delete Status");
		fileDeleteTable = new JMenuItem("Delete Table Status");
		fileExport = new JMenuItem("Export to XML ...");
		fileQuit = new JMenuItem("Quit");
		fileDeleteStatus.addActionListener(menuHandeler);
		fileDeleteTable.addActionListener(menuHandeler);
		fileExport.addActionListener(menuHandeler);
		fileQuit.addActionListener(menuHandeler);
		file.add(fileDeleteStatus);
		file.add(fileDeleteTable);
		file.add(fileExport);
		file.add(fileQuit);
		menu.add(file);
		// Creates the Generate Menu
		generate = new JMenu("Generate");
		generateWordFrequencyList = new JMenuItem("Word Frequency List");
		generateTopTrendingList = new JMenuItem("Top Trending List");
		generateTopTrendingList.addActionListener(menuHandeler);
		generateWordFrequencyList.addActionListener(menuHandeler);
		generate.add(generateWordFrequencyList);
		generate.add(generateTopTrendingList);
		menu.add(generate);

		favoritesMenu = new JMenu("Favorites");

		addFavorites = new JMenuItem("Add to Favorites");
		addFavorites.addActionListener(menuHandeler);
		removeFavorites = new JMenuItem("Remove Favorite");
		removeFavorites.addActionListener(menuHandeler);
		favoritesMenu.add(addFavorites);
		favoritesMenu.add(removeFavorites);
		menu.add(favoritesMenu);
		// Create the Help Menu
		help = new JMenu("Help");
		helpAbout = new JMenuItem("About...");
		helpAbout.addActionListener(menuHandeler);
		help.add(helpAbout);
		menu.add(help);
		// Add Menu to the GUI
		guiFrame.setJMenuBar(menu);
	}

	class ButtonListener implements ActionListener {
		public void actionPerformed(final ActionEvent e) {
			if (e.getSource().equals(LoginButton)) {
				try {
					tabs.removeAll();
					engine.logout();

					engine.login();
					profileTabInit();
					postTimeTabInit();
					followerTabInit();
					favoritesTabInit();
					MyTweetsTabInit();
					FriendsListTabInit();

					tabs.addTab("Profile", ProfilePanel);
					tabs.addTab("Post Tweet/Timeline", PostTimePanel);
					tabs.addTab("Followers", TwitResults);
					tabs.addTab("Friends List", FriendsListPanel);
					tabs.addTab("Favorites", FavoritesPanel);
					tabs.addTab("Your Tweets", MyTweetsPanel);

					guiFrame.add(tabs);

				} catch (FileNotFoundException e1) {
					e1.printStackTrace();
				} catch (IllegalStateException e1) {
					e1.printStackTrace();
				} catch (TwitterException e1) {
					e1.printStackTrace();
				} catch (IOException e1) {
					e1.printStackTrace();
				} catch (Exception e1) {
					e1.printStackTrace();
				}
			}
			if (e.getSource().equals(SignoutButton)) {
				engine.logout();

				System.exit(0);
			}

			if (e.getSource().equals(TweetButton)) {
				try {
					String post = updateTextBox.getText();
					engine.postStatus(post);
					updateTextBox.setText("");

				} catch (TwitterException e1) {
					e1.printStackTrace();
				}
			}
			if (e.getSource().equals(updateTimelineButton)) {
				try {
					tabs.remove(1);
					postTimeTabInit();

					tabs.insertTab("Post Tweet/Timeline", null, PostTimePanel,
							null, 1);

					tabs.setSelectedIndex(1);
				} catch (IllegalStateException  e1) {
					e1.printStackTrace();
				} catch (TwitterException e1) {
					// TODO Auto-generated catch block
					e1.printStackTrace();
				}
			}

			if (e.getSource().equals(UpdateFavoritesButton)) {

				try {

					tabs.remove(4);
					favoritesTabInit();

					tabs.insertTab("Favorites", null, FavoritesPanel, null, 4);

					tabs.setSelectedIndex(4);

				} catch (IllegalStateException e1) {
					e1.printStackTrace();
				} catch (TwitterException e1) {
					// TODO Auto-generated catch block
					e1.printStackTrace();
				}
			}

			if (e.getSource().equals(MyTweetsFavoriteButton)) {
				try {
					engine.favoriteTweet(MyTweetsStatusIds[list
							.getSelectedIndex()]);
				} catch (NumberFormatException  e1) {
					e1.printStackTrace();
				} catch (TwitterException e1) {
					// TODO Auto-generated catch block
					e1.printStackTrace();
				}

			}

			if (e.getSource().equals(DeleteMyTweetButton)) {

				try {
					engine.deleteStatus(MyTweetsStatusIds[list
							.getSelectedIndex()]);
				} catch (TwitterException e1) {
					e1.printStackTrace();
				}

			}

			if (e.getSource().equals(VeiwProfile)) {
				try {
					new FriendVeiwer(userIds[FriendsList.getSelectedIndex()],
							engine);
				} catch (TwitterException e1) {
					e1.printStackTrace();
				} catch (Exception e1) {
					e1.printStackTrace();
				}
			}

			if (e.getSource().equals(RemoveFriend)) {
				try {
					engine.removefriend(userIds[FriendsList.getSelectedIndex()]);
				} catch (TwitterException e1) {
					e1.printStackTrace();
				}
			}

			if (e.getSource().equals(ViewMessagesButton)) {
				try {
					new DirectMessageViewer(engine);
				} catch (TwitterException e1) {
					e1.printStackTrace();
				}
			}

			if (e.getSource().equals(TimelineFavoriteButton)) {
				try {
					engine.favoriteTweet(MyTimeLineStatusIds[TimelineList
							.getSelectedIndex()]);
				} catch (NumberFormatException e1) {
					e1.printStackTrace();
				} catch (TwitterException e1) {
					if (e1.getErrorCode() == 139) {
						JOptionPane.showMessageDialog(guiFrame,
								"You have already favorited that Tweet",
								"Error", JOptionPane.ERROR_MESSAGE);
					}
				}
			}

			if (e.getSource().equals(DeleteFavoriteButton)) {
				try {
					engine.deleteStatus(MyFavoriteStatusIds[FavoritesList
							.getSelectedIndex()]);
				} catch (TwitterException e1) {
					e1.printStackTrace();
				}
			}

			if (e.getSource().equals(UpdateFriendsList)) {
				tabs.remove(3);
				try {
					FriendsListTabInit();
				} catch (TwitterException e1) {

					e1.printStackTrace();
				}

				tabs.insertTab("Friends", null, FriendsListPanel, null, 3);

				tabs.setSelectedIndex(3);
			}

			if (e.getSource().equals(UpdateMyTweetsList)) {
				tabs.remove(5);
				try {
					favoritesTabInit();
				} catch (TwitterException e1) {
					e1.printStackTrace();
				}

				tabs.insertTab("My Tweets", null, MyTweetsPanel, null, 5);

				tabs.setSelectedIndex(5);
			}
		}
	}

	/**
	 * Handles all of the actions in the JMenuBar
	 */
	private ActionListener menuHandeler = new ActionListener() {
		@Override
		public void actionPerformed(final ActionEvent e) {
			if (e.getActionCommand().equals("Delete Status")) {
				int i = 0;

				try {
					engine.deleteStatus(i);
				} catch (TwitterException e1) {
					e1.printStackTrace();
				}
			}
			if (e.getActionCommand().equals("Delete Table Status")) {
				engine.deleteTweet();
			}
			if (e.getActionCommand().equals("Quit")) {
				System.exit(0);
			}
			if (e.getActionCommand().equals("Add to Favorites")) {
				engine.addFavorite();
			}
			if (e.getActionCommand().equals("Remove Favorite")) {
				engine.removeFavorite();
			}
			if (e.getActionCommand().equals("About...")) {
				JOptionPane.showMessageDialog(null,
						"Produced by\n Benjamin Summers \n"
								+ "			 Kevin Anderson     \n"
								+ "			 Seth Hilaski       \n"
								+ "			 Trent Newberry     \n"
								+ "          4/14/2013          \n"
								+ "          For a CIS350 Project");
			}
			if (e.getActionCommand().equals("Top Trending List")) {
				String[] options = engine.getTrendsLocations();
				String choice;
				choice = (String) JOptionPane
						.showInputDialog(guiFrame, "Pick a Trend Location",
								"Trend List", JOptionPane.OK_CANCEL_OPTION,
								null, options, options[0]);
				if (choice != null) {
					trendslist = engine.getPlaceTrends(choice).getTrends();
					updateTrendsArea();
				}
			}

		}
	};

	/**
	 * runs the main application starting with TwitterGUI which instantiates all
	 * other components that are part of the GUI
	 * 
	 * @param args
	 */
	public static void main(final String[] args) {
		java.awt.EventQueue.invokeLater(new Runnable() {
			public void run() {

				try {
					new TwitterGUI();
				} catch (FileNotFoundException e) {
					e.printStackTrace();
				} catch (IllegalStateException e) {
					e.printStackTrace();
				} catch (IOException e) {
					e.printStackTrace();
				} catch (Exception e) {
					e.printStackTrace();
				}
			}
		});
	}

}

package gui;

import java.awt.BorderLayout;
import java.awt.Color;
import java.awt.GridLayout;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import java.net.MalformedURLException;
import java.net.URL;
import java.util.List;

import javax.imageio.ImageIO;
import javax.swing.BorderFactory;
import javax.swing.DefaultListModel;
import javax.swing.ImageIcon;
import javax.swing.JButton;
import javax.swing.JFrame;
import javax.swing.JLabel;
import javax.swing.JList;
import javax.swing.JOptionPane;
import javax.swing.JPanel;
import javax.swing.JScrollPane;
import javax.swing.JTabbedPane;
import javax.swing.SwingConstants;
import javax.swing.border.Border;

import twitter4j.Status;
import twitter4j.TwitterException;
import twitter4j.User;

import engine.TwitterEngine;

public class FriendVeiwer {

	private JFrame GUI;

	private JTabbedPane tabs;

	private JPanel ProfilePanel;
	private JPanel TimelinePanel;
	private JPanel FriendsListPanel;
	private JPanel FavoritesPanel;
	private JPanel FriendsOptionsPanel;

	private JButton FavoriteButton;
	private JButton ViewProfileButton;
	private JButton FavoriteTweetButton;
	private JButton DirectMessageButton;
	private JButton CreateFriendButton;

	private JList<String> TimelineList;
	private JList<String> FriendsList;
	private JList<String> FavoritesList;

	private List<Status> statuses;
	private List<User> users;

	private String[] FriendsNamesList;
	private long[] UserIds;
	private long[] statusIds;

	private long userId;

	private TwitterEngine engine;

	public FriendVeiwer(long userIds, TwitterEngine engine) throws Exception {

		userId = userIds;

		GUI = new JFrame();

		this.engine = engine;

		tabs = new JTabbedPane();

		TimelineTabInit();
		favoritesTabInit();
		FriendsListTabInit();
		profileTabInit(userIds);

		final URL url = new URL(
				"http://cdn-ak.f.st-hatena.com/images/fotolife/r/rinrinbell/20091225/20091225215812.png");
		GUI.setIconImage(ImageIO.read(url));

		tabs.addTab("Profile", ProfilePanel);
		tabs.addTab(this.engine.getfriendsName(userIds) + "'s Timeline",
				TimelinePanel);
		tabs.addTab(this.engine.getfriendsName(userIds) + "'s Favorite Tweets",
				FavoritesPanel);
		tabs.addTab("Friends", FriendsListPanel);

		GUI.setSize(800, 450);

		GUI.setTitle("Twitter for " + engine.getfriendsName(userId));

		GUI.add(tabs);

		GUI.setVisible(true);

	}

	private void profileTabInit(long userIds) throws IllegalStateException,
			MalformedURLException, TwitterException {
		Border blackline = BorderFactory.createLineBorder(Color.black);

		ButtonListener listener = new ButtonListener();

		ProfilePanel = new JPanel();
		FriendsOptionsPanel = new JPanel();

		ProfilePanel.setLayout(new GridLayout(2, 1));

		JPanel top = new JPanel();
		top.setLayout(new GridLayout(1, 1));
		top.setBorder(blackline);
		top.setSize(200, 200);

		JPanel top2 = new JPanel();
		top2.setLayout(new GridLayout(2, 1));
		top.setBorder(blackline);

		JPanel bottom = new JPanel();
		bottom.setBorder(blackline);
		JPanel user = new JPanel();
		user.setLayout(new BorderLayout());
		user.setBorder(blackline);

		ImageIcon img = engine.getfriendProfileImage(userIds);

		JLabel userIcon = new JLabel(img);
		userIcon.setBorder(blackline);
		JLabel screenname = new JLabel(engine.getScreenName());
		screenname.setHorizontalAlignment(SwingConstants.CENTER);
		screenname.setBorder(blackline);
		screenname.setSize(20, 12);
		JLabel realname = new JLabel(engine.getRealName(userId));
		realname.setBorder(blackline);
		realname.setHorizontalAlignment(SwingConstants.CENTER);
		realname.setSize(20, 12);
		JLabel tweets = new JLabel("Tweets: " + engine.getTweets());
		JLabel followers = new JLabel("Followers: "
				+ engine.getFollowersCount());
		JLabel following = new JLabel("Following: "
				+ engine.getFollowingCount());
		JLabel rateLimit = new JLabel("Rate Limit: " + engine.getRateLimit());
		JLabel rateLimitRemaining = new JLabel("Rate Limit Remaining: "
				+ engine.getRateLimitRemaining());

		DirectMessageButton = new JButton("Send Direct Message");
		DirectMessageButton.addActionListener(listener);
		CreateFriendButton = new JButton("Follow");
		CreateFriendButton.addActionListener(listener);
		FriendsOptionsPanel.add(CreateFriendButton);
		FriendsOptionsPanel.add(DirectMessageButton);

		top.add(userIcon);
		top2.add(realname);
		top2.add(screenname);
		bottom.add(tweets);
		bottom.add(followers);
		bottom.add(following);
		bottom.add(rateLimit);
		bottom.add(rateLimitRemaining);

		user.add(top, BorderLayout.PAGE_START);
		user.add(top2, BorderLayout.CENTER);
		user.add(bottom, BorderLayout.PAGE_END);
		ProfilePanel.add(user, BorderLayout.CENTER);
		ProfilePanel.add(FriendsOptionsPanel, BorderLayout.PAGE_END);
	}

	private void FriendsListTabInit() throws TwitterException {
		FriendsListPanel = new JPanel();
		ViewProfileButton = new JButton("View Profile");
		ButtonListener listener = new ButtonListener();

		ViewProfileButton.addActionListener(listener);

		FriendsNamesList = new String[200];
		UserIds = new long[200];
		int count = 0;
		users = engine.getFriendsList(userId);
		for (User user : users) {
			FriendsNamesList[count] = user.getName();
			UserIds[count] = user.getId();
			count++;
		}

		FriendsList = new JList<String>(FriendsNamesList);
		FriendsList.setFixedCellHeight(30);

		JScrollPane scrollpane = new JScrollPane(FriendsList);
		FriendsListPanel.add(scrollpane);
		FriendsListPanel.add(ViewProfileButton);
	}

	private void favoritesTabInit() throws TwitterException {
		FavoritesPanel = new JPanel();

		ButtonListener listener = new ButtonListener();

		String html1 = "<html><body style='width: ";
		String html2 = "px'>";

		FavoriteTweetButton = new JButton("Favorite");

		FavoriteTweetButton.addActionListener(listener);

		statuses = engine.getFriendsFavoriteTweets(userId);
		long[] statusIds = new long[200];

		DefaultListModel<String> model = new DefaultListModel<String>();
		FavoritesList = new JList<String>(model);
		FavoritesList.setFixedCellHeight(50);

		int count = 0;

		for (Status status : statuses) {
			model.addElement(html1 + "400" + html2 + status.getUser().getName()
					+ ":" + status.getText());
			statusIds[count] = status.getId();
			count++;
		}

		JScrollPane FavoritePane = new JScrollPane(FavoritesList);

		FavoritesPanel.add(FavoritePane);
		FavoritesPanel.add(FavoriteTweetButton);
	}

	private void TimelineTabInit() throws TwitterException {
		ButtonListener listener = new ButtonListener();

		String html1 = "<html><body style='width: ";
		String html2 = "px'>";

		DefaultListModel<String> model = new DefaultListModel<String>();
		TimelineList = new JList<String>(model);
		TimelineList.setFixedCellHeight(50);

		TimelinePanel = new JPanel();

		FavoriteButton = new JButton("Favorite");
		FavoriteButton.addActionListener(listener);

		statusIds = new long[200];
		int count = 0;
		statuses = engine.getfriendsTimeline(userId);

		for (Status status : statuses) {
			model.addElement(html1 + "500" + html2 + engine.getRealName(userId)
					+ ": " + status.getText() + "\n");
			model.addElement(" ");
			statusIds[count] = status.getId();
			count += 2;

		}

		JScrollPane TimeLinePane = new JScrollPane(TimelineList);
		TimelinePanel.add(TimeLinePane);
		TimelinePanel.add(FavoriteButton);
	}

	class ButtonListener implements ActionListener {
		public void actionPerformed(final ActionEvent e) {

			if (e.getSource().equals(FavoriteButton)) {
				try {
					engine.favoriteTweet(statusIds[TimelineList
							.getSelectedIndex()]);
				} catch (NumberFormatException e1) {
					JOptionPane.showMessageDialog(GUI,
							"Please do not select the white space.  Thank you");
				} catch (TwitterException e1) {
					// TODO Auto-generated catch block
					e1.printStackTrace();
				}
			}
			if (e.getSource().equals(FavoriteTweetButton)) {
				try {
					engine.favoriteTweet(statusIds[FavoritesList
							.getSelectedIndex()]);
				} catch (NumberFormatException  e1) {
					JOptionPane.showMessageDialog(GUI,
							"Please do not select the white space.  Thank you");
				} catch (TwitterException e1) {
					// TODO Auto-generated catch block
					e1.printStackTrace();
				}
			}

			if (e.getSource().equals(ViewProfileButton)) {
				try {
					new FriendVeiwer(UserIds[FriendsList.getSelectedIndex()],
							engine);
				} catch (Exception e1) {
					e1.printStackTrace();
				}
			}

			if (e.getSource().equals(CreateFriendButton)) {
				try {
					engine.CreateFriendship(userId);
				} catch (TwitterException e1) {
					e1.printStackTrace();
				}
			}

			if (e.getSource().equals(DirectMessageButton)) {
				new DirectMessageSender(userId, engine);
			}
		}
	}
}

package gui;

import java.awt.BorderLayout;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import java.util.List;

import javax.swing.DefaultListModel;
import javax.swing.JButton;
import javax.swing.JFrame;
import javax.swing.JList;
import javax.swing.JPanel;
import javax.swing.JScrollPane;

import twitter4j.DirectMessage;
import twitter4j.TwitterException;

import engine.TwitterEngine;

public class DirectMessageViewer {

	private JFrame MessageViewer;
	
	private JPanel MessagePanel;
	
	private List<DirectMessage> Messages;
	
	private JList<String> MessagesList;
	
	private JButton DeleteMessageButton;
	
	private long[] MessageIds;
	
	TwitterEngine engine;
	
	public DirectMessageViewer(TwitterEngine engine) throws TwitterException{
		this.engine = engine;
		
		DefaultListModel<String> model = new DefaultListModel<String>();
		ButtonListener listener = new ButtonListener();
		
		MessageViewer = new JFrame("Messages");
		
		MessagePanel = new JPanel();
		MessagePanel.setLayout(new BorderLayout());
		
		JPanel ButtonsPanel = new JPanel();
		
		Messages = engine.getDirectMessages();
		
		MessagesList = new JList<String>(model);
		MessagesList.setFixedCellHeight(40);
		
		
		MessageIds = new long[50];
		
		int count = 0;
		
		for(DirectMessage message : Messages){
			model.addElement(message.getText());
			MessageIds[count] = message.getId();
			count++;
		}
		
		JScrollPane scrollpane = new JScrollPane(MessagesList);
		
		DeleteMessageButton = new JButton("Delete Message");
		DeleteMessageButton.addActionListener(listener);
		
		ButtonsPanel.add(DeleteMessageButton, BorderLayout.PAGE_END);	
		
		MessagePanel.add(scrollpane, BorderLayout.CENTER);
		MessagePanel.add(ButtonsPanel, BorderLayout.PAGE_END);
		
		MessageViewer.add(MessagePanel);
		
		MessageViewer.setSize(800, 450);
		
		MessageViewer.setVisible(true);
	}
	
	class ButtonListener implements ActionListener {
		public void actionPerformed(final ActionEvent e) {
			if(e.getSource().equals(DeleteMessageButton)){
				try {
					engine.deleteMessage(MessageIds[MessagesList.getSelectedIndex()]);
				} catch (TwitterException e1) {
					e1.printStackTrace();
				}
			}
		}
	}
}

package gui;

import java.awt.BorderLayout;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import java.awt.event.WindowEvent;

import javax.swing.JButton;
import javax.swing.JFrame;
import javax.swing.JOptionPane;
import javax.swing.JPanel;
import javax.swing.JScrollPane;
import javax.swing.JTextArea;
import twitter4j.TwitterException;

import engine.TwitterEngine;

public class DirectMessageSender {

	private JFrame MessageEditor;

	private JPanel MessagePanel;

	private JTextArea MessageArea;

	private JButton SendMessageButton;

	private TwitterEngine engine;

	private String message;

	private long userId;

	public DirectMessageSender(long userId, TwitterEngine engine) {
		this.engine = engine;
		this.userId = userId;
		ButtonListener listener = new ButtonListener();

		MessageEditor = new JFrame("Send Direct Message");

		MessagePanel = new JPanel();
		MessagePanel.setLayout(new BorderLayout());

		MessageArea = new JTextArea();
		MessageArea.setLineWrap(true);

		JScrollPane scrollpane = new JScrollPane(MessageArea);

		SendMessageButton = new JButton("Send Message");
		SendMessageButton.addActionListener(listener);

		MessagePanel.add(scrollpane, BorderLayout.CENTER);
		MessagePanel.add(SendMessageButton, BorderLayout.PAGE_END);

		MessageEditor.add(MessagePanel);

		MessageEditor.setSize(300, 200);

		MessageEditor.setVisible(true);
	}

	class ButtonListener implements ActionListener {
		public void actionPerformed(final ActionEvent e) {

			if (e.getSource().equals(SendMessageButton)) {
				message = MessageArea.getText();
				try {
					engine.sendDirectMessage(userId, message);
					MessageEditor.dispatchEvent(new WindowEvent(MessageEditor,
							WindowEvent.WINDOW_CLOSING));
				} catch (TwitterException e1) {
					if (e1.exceededRateLimitation()) {
						JOptionPane.showMessageDialog(MessageEditor,
								"You message is longer than 140 characters."
										+ "  Please make your message shorter");
					}
					e1.printStackTrace();

				}
			}
		}
	}
}

package engine;

import java.net.MalformedURLException;
import java.util.Date;
import java.util.List;

import javax.swing.ImageIcon;

import twitter4j.AccountSettings;
import twitter4j.DirectMessage;
import twitter4j.Status;
import twitter4j.Trends;
import twitter4j.TwitterException;
import twitter4j.User;

import model.Tweet;
import model.TwitModel;

/**
 * TwitterEngine.class
 * 
 * Intermediary between the GUI and model
 * 
 * @author Ben
 */
public class TwitterEngine {

	private TwitModel model;
	private int index;

	// private String post;

	/**
	 * Constructs the Twitter Engine
	 * 
	 * @throws TwitterException
	 */
	public TwitterEngine() throws TwitterException {
		model = new TwitModel();
	}

	/**
	 * gets the model
	 * 
	 * @return model
	 */
	public final TwitModel getModel() {
		return model;
	}

	/**
	 * gets a users status
	 * 
	 * @param userName
	 *            - the user being searched
	 */
	public final void getStatus(final String userName) {
		model.retrieveStatus(userName);
	}

	/**
	 * gets a users timeline
	 * 
	 * @return
	 * 
	 * @throws TwitterException
	 */
	public final List<Status> getTimeline() throws TwitterException {
		return model.retriveTimeline();
	}

	/**
	 * addTweets creates a twitter Status in the table
	 * 
	 * @param date
	 *            - createdAt
	 * @param loginName
	 *            - user
	 * @param displayName
	 *            - name that is shown
	 * @param friends
	 *            - how many friends
	 * @param followers
	 *            - how many followers
	 * @param text
	 *            - status text
	 */
	public final void addTweet(final Date date, final String loginName,
			final String displayName, final int friends, final int followers,
			final String text, final boolean fave) {
		Tweet t = new Tweet(date, loginName, displayName, friends, followers,
				text, fave);
		model.add(t);
	}

	/**
	 * sends the inputs for a user to authenticate their current session
	 * 
	 * @param userName
	 *            - user at the screen
	 * @param consumerKey
	 *            - their consumer key
	 * @param consumerSecret
	 *            - their consumer secret key
	 * @param accessToken
	 *            - their access token
	 * @param accessTokenS
	 *            - their access token secret
	 * @throws Exception
	 */
	public final void login() throws Exception {

		model.authenticate();
	}

	public final void logout() {

		model.logout();
	}

	/**
	 * gets the current array lists size
	 * 
	 * @return - checks an array lists size
	 */
	public final int getArrayListSize() {
		return model.getArrayListSize();
	}

	/**
	 * gets the text for a selected users status
	 * 
	 * @param index
	 *            - the current spot on a table highlighted
	 * @return - the status of that highlighted table row
	 */
	public final String getDisplayStatis(final int idex) {
		this.index = idex;
		return model.retriveDisplayStatis(index);
	}

	/**
	 * Searches for a particular keyword
	 * 
	 * @param keyWord
	 *            - keyword for the search
	 */
	public final void getWordSearch(final String keyWord) {
		model.getWordSearch(keyWord);
	}

	/**
	 * Searches for a phrase
	 * 
	 * @param keyWord
	 *            - the keyword
	 * @param phrase
	 *            - the phrase
	 */
	public final void getPhraseSearch(final String keyWord, final String phrase) {
		model.getPhraseSearch(keyWord, phrase);
	}

	/**
	 * removes a tweet from the table
	 */
	public final void deleteTweet() {
		model.remove(index);
	}

	/**
	 * removes current status
	 * 
	 * @throws TwitterException
	 */
	public void deleteStatus(long statusId) throws TwitterException {
		model.destoryStatus(statusId);
	}

	/**
	 * Posts a new status
	 * 
	 * @param post2
	 *            - 140 character or less post
	 * @return
	 * @return
	 * @throws TwitterException
	 */
	public final void postStatus(final String post) throws TwitterException {
		model.updateStatus(post);
	}

	public final ImageIcon getProfileImage() throws IllegalStateException,
			TwitterException, MalformedURLException {
		return model.getProfileImage();
	}

	public final String getRealName(long userId) throws TwitterException {
		return model.getRealName(userId);
	}

	public final String getScreenName() {
		return model.getScreenName();
	}

	public final int getTweets() {
		return model.getTweets();
	}

	public final int getFollowersCount() {
		return model.getFollowersCount();
	}

	public final int getFollowingCount() {
		return model.getFollowingCount();
	}

	public final int getRateLimit() {
		return model.getRateLimit();
	}

	public final int getRateLimitRemaining() {
		return model.getLimitRemaining();
	}

	public final boolean getFavoriteStatus() {
		return model.getFavoriteStatus();
	}

	public final void addFavorite() {
		model.addFavoriteUser(index);
	}

	public final void removeFavorite() {
		model.removeFavoriteUser(index);
	}

	public List<Status> getFavoriteTweets() throws TwitterException {
		return model.getfavoriteTweets();
	}

	public AccountSettings getAccountSettings() throws TwitterException {
		return model.getAccountSettings();
	}

	public List<Status> getMyTweets() throws TwitterException {
		return model.getMyTweets();
	}

	public void favoriteTweet(long statusIds) throws NumberFormatException,
			TwitterException {
		model.favoriteTweet(statusIds);
	}

	public List<User> getFriendsList(long userId) throws TwitterException {
		return model.getFriendsList(userId);
	}

	public void removefriend(long userId) throws TwitterException {
		model.removeFriend(userId);
	}

	public String getfriendsName(long UserId) throws TwitterException {
		return model.getfriendsName(UserId);
	}

	public ImageIcon getfriendProfileImage(long UserId)
			throws MalformedURLException, TwitterException {
		return model.getfriendProfileImage(UserId);
	}

	public List<Status> getfriendsTimeline(long userIds)
			throws TwitterException {
		return model.getfriendsTimeline(userIds);
	}

	public List<Status> getFriendsFavoriteTweets(long userId)
			throws TwitterException {
		return model.getFriendsFavoriteTweets(userId);
	}

	public Long getuserid() throws IllegalStateException, TwitterException {
		return model.getuserId();
	}

	public void CreateFriendship(long userId) throws TwitterException {
		model.createFriendship(userId);
	}

	public void sendDirectMessage(long userId, String message)
			throws TwitterException {
		model.sendDirectMessage(userId, message);
	}

	public final String[] getTrendsLocations() {
		return model.getTrendsLocationNames();
	}

	public final Trends getPlaceTrends(String location) {
		return model.getLocationTrends(location);
	}

	public List<DirectMessage> getDirectMessages() throws TwitterException {
		return model.getDirectMessages();
	}

	public void deleteMessage(long MessageId) throws TwitterException {
		model.deleteMessage(MessageId);
	}

}

package engine;

/**
 * @author Ben
 *
 * Creates the Interface for the Twitter Engine class
 */
public interface ITweet {
	java.util.Date getCreatedAt();
	String getLoginName();
	String getDisplayName();
	String getText();
	int getFriendsCount();
	int getFollowersCount();
	String getFavorite();
}
